mapper.select:

        select U.USER_ID, USER_PW, USER_NAME, USER_EMAIL, USER_TEL, USER_TEL_SUB, USER_BIRTH ,JOINDATE, USER_GRADE, ACTIVE, REPLACE(NVL(max(RESERVATION_STATE),'X'),'N','O') RES_STATE
		    from TB_USER U 
		    LEFT JOIN ( select user_id, RESERVATION_STATE from TB_RESERVATION WHERE RESERVATION_STATE='N' ) R
		ON U.user_id = R.user_id  



			 		U.USER_ID LIKE '%'||#{keyword}||'%' 


			 		USER_NAME LIKE '%'||#{keyword}||'%'


			 		USER_TEL LIKE '%'||#{keyword}||'%'


			 		USER_EMAIL LIKE '%'||#{keyword}||'%'




					AND(

						USER_GRADE=#{grade1}


						OR USER_GRADE=#{grade2}


						OR USER_GRADE=#{grade3}

					)




				AND REPLACE(NVL(RESERVATION_STATE,'X'),'N','O') = #{res_state}



		GROUP BY U.USER_ID, USER_PW, USER_NAME, USER_EMAIL, USER_TEL, USER_TEL_SUB, USER_BIRTH ,JOINDATE, USER_GRADE, ACTIVE
		order by joinDate desc
