mapper.select:
SELECT * FROM ( SELECT ROWNUM as recNum, to_char(r.pay_time, 'yyyy-mm-dd') as pay_time, to_char(r.reservation_st, 'yyyy-mm-dd') as reservation_st, r.reservation_state,r.reservation_num, u.user_name, u.user_tel, p.pet_name from tb_reservation r, tb_user u, tb_petservice p where r.reservation_num = p.reservation_num and u.user_id = r.user_id and r.user_id = #{user_id}
and ${colName} like '%'|| #{searchWord} ||'%'
) where recNum between(#{section}-1)*100+(#{pageNum}-1)*10+1 and (#{section}-1)*100+#{pageNum}*10 order by pay_time

		    SELECT * FROM ( 
			SELECT ROWNUM as recNum, to_char(r.pay_time, 'yyyy-mm-dd') as pay_time, to_char(r.reservation_st, 'yyyy-mm-dd') as reservation_st, r.reservation_state,r.reservation_num, u.user_name, u.user_tel, p.pet_name
					from tb_reservation r, tb_user u, tb_petservice p
					where r.reservation_num = p.reservation_num and u.user_id = r.user_id and r.user_id = #{user_id}


					 and to_char(r.pay_time, 'yyyy-mm-dd') between #{startDate} and #{endDate}


					and to_char(r.pay_time, 'yyyy-mm-dd') between #{startDate} and #{endDate}
					and ${colName} like '%'|| #{searchWord} ||'%'




			)
             	where recNum between(#{section}-1)*100+(#{pageNum}-1)*10+1 and (#{section}-1)*100+#{pageNum}*10
		        order by pay_time


select review_date, review_num, review_title from tb_review
	         where user_id=#{user_id}
	         order by review_date desc

mapper.update:
   update tb_reservation
	   set reservation_state='C'
   where reservation_num=#{res_num}

mapper.select:
         select count(p.reservation_num)
	         from tb_reservation r
	         left join (select user_id, user_name, user_tel from tb_user) u
         on u.user_id = r.user_id
	         left join (select reservation_num, pet_name from tb_petservice) p
         on r.reservation_num = p.reservation_num
         where r.user_id = #{user_id}


				 and to_char(r.pay_time, 'yyyy-mm-dd') between #{startDate} and #{endDate}


				and to_char(r.pay_time, 'yyyy-mm-dd') between #{startDate} and #{endDate}
				and ${colName} like '%'|| #{searchWord} ||'%'
