mapper.select:
	select to_char(r.reservation_st, 'yyyy-mm-dd') as reservation_st, r.reservation_state, r.reservation_num, u.user_name, u.user_tel, p.pet_name
	from tb_reservation r, tb_user u, tb_petservice p
	                 where r.reservation_num between(#{section}-1)*100+(#{pageNum}-1)*10+1 and (#{section}-1)*100+#{pageNum}*10
	                 and r.reservation_num = p.reservation_num and u.user_id = r.user_id and u.user_id = #{user_id}


			 and ${colName} like '%'|| #{searchWord} ||'%'



			 and to_char(r.reservation_st, 'yyyy-mm-dd') between #{startDate} and #{endDate} 



			and to_char(r.reservation_st, 'yyyy-mm-dd') between #{startDate} and #{endDate}
			and ${colName} like '%'|| #{searchWord} ||'%'




         order by r.reservation_st desc


select review_date, review_num, review_title, review_con from tb_review
	         where user_id=#{user_id}
	         order by review_date desc

mapper.update:
   update tb_reservation
	   set reservation_state='C'
   where reservation_num=#{res_num}
